mapper.select:
select diary_id, member_id, year, month, day,memo,sentiment
from diary
where member_id = #{member_id}

mapper.insert:
insert into diary(
	diary_id, diary_id, year, month, day,memo,sentiment
) values (
	#{diary_id}, #{diary_id}, #{year}, #{month},#{day}, #{memo}, #{sentiment}
)

mapper.update:

update diary
set memo = #{memo} , sentiment = #{sentiment}
where diary_id = #{diary_id}
